package com.demo.jetweatherapp.dataimport androidx.room.*import com.demo.jetweatherapp.model.Favoriteimport kotlinx.coroutines.flow.Flow@Daointerface WeatherDao {    @Query("SELECT * from fav_tbl")    fun getFavorites(): Flow<List<Favorite>>    @Query("SELECT * from fav_tbl WHERE city =:city")    suspend fun getFavById(city: String): Favorite    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun insertFavorite(favorite: Favorite)    @Update(onConflict = OnConflictStrategy.REPLACE)    suspend fun updateFavorite(favorite: Favorite)    @Delete    suspend fun deleteFavorite(favorite: Favorite)    @Query("DELETE from fav_tbl")    suspend fun deleteAllFavorites()}