package com.demo.jetweatherapp.navigationimport androidx.compose.runtime.Composableimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport com.demo.jetweatherapp.screens.aboutScreen.WeatherAboutScreenimport com.demo.jetweatherapp.screens.favoriteScreen.WeatherFavoriteScreenimport com.demo.jetweatherapp.screens.mainScreen.WeatherMainScreenimport com.demo.jetweatherapp.screens.searchScreen.WeatherSearchScreenimport com.demo.jetweatherapp.screens.settingsScreen.WeatherSettingsScreenimport com.demo.jetweatherapp.screens.splashScreen.WeatherSplashScreen@Composablefun WeatherNavigation() {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = WeatherScreens.SplashScreen.name) {        composable(WeatherScreens.SplashScreen.name) {            WeatherSplashScreen(navController = navController)        }        composable(WeatherScreens.MainScreen.name) {            WeatherMainScreen(navController = navController)//, mainViewModel)        }        composable(WeatherScreens.AboutScreen.name) {            WeatherAboutScreen(navController = navController)        }        composable(WeatherScreens.FavoritesScreen.name) {            WeatherFavoriteScreen(navController = navController)        }        composable(WeatherScreens.SearchScreen.name) {            WeatherSearchScreen(navController = navController)        }        composable(WeatherScreens.SettingsScreen.name) {            WeatherSettingsScreen(navController = navController)        }    }}