package com.demo.jetweatherapp.navigationimport androidx.compose.runtime.Composableimport androidx.hilt.navigation.compose.hiltViewModelimport androidx.navigation.NavTypeimport androidx.navigation.compose.NavHostimport androidx.navigation.compose.composableimport androidx.navigation.compose.rememberNavControllerimport androidx.navigation.navArgumentimport com.demo.jetweatherapp.screens.aboutScreen.WeatherAboutScreenimport com.demo.jetweatherapp.screens.favoriteScreen.WeatherFavoriteScreenimport com.demo.jetweatherapp.screens.mainScreen.MainViewModelimport com.demo.jetweatherapp.screens.mainScreen.WeatherMainScreenimport com.demo.jetweatherapp.screens.searchScreen.WeatherSearchScreenimport com.demo.jetweatherapp.screens.settingsScreen.WeatherSettingsScreenimport com.demo.jetweatherapp.screens.splashScreen.WeatherSplashScreen@Composablefun WeatherNavigation() {    val navController = rememberNavController()    NavHost(navController = navController, startDestination = WeatherScreens.SplashScreen.name) {        composable(WeatherScreens.SplashScreen.name) {            WeatherSplashScreen(navController = navController)        }        //www.google.es/cityname="elche"        val key = "city"        val route = WeatherScreens.MainScreen.name        composable(            "$route/{city}", arguments = listOf(                navArgument(name = "city") {                    type = NavType.StringType                }            )        ) { navBack ->            navBack.arguments?.getString("city").let { city ->                val mainViewModel = hiltViewModel<MainViewModel>()                WeatherMainScreen(navController = navController, mainViewModel, city = city)            }        }        composable(WeatherScreens.AboutScreen.name) {            WeatherAboutScreen(navController = navController)        }        composable(WeatherScreens.FavoritesScreen.name) {            WeatherFavoriteScreen(navController = navController)        }        composable(WeatherScreens.SearchScreen.name) {            WeatherSearchScreen(navController = navController)        }        composable(WeatherScreens.SettingsScreen.name) {            WeatherSettingsScreen(navController = navController)        }    }}