package com.demo.jetweatherapp.navigationenum class WeatherScreens {    SplashScreen,    MainScreen,    AboutScreen,    FavoritesScreen,    SearchScreen,    SettingsScreen;    companion object {        fun fromRoute(route: String?): WeatherScreens        = when(route?.substringBefore("/")){            SplashScreen.name -> SplashScreen            MainScreen.name -> MainScreen            AboutScreen.name -> AboutScreen            FavoritesScreen.name -> FavoritesScreen            SearchScreen.name -> SearchScreen            SettingsScreen.name -> SettingsScreen            null -> MainScreen            else -> throw IllegalArgumentException("Unknown route: $route")        }    }}