package com.demo.jetweatherapp.repositoryimport android.util.Logimport com.demo.jetweatherapp.data.DataOrExceptionimport com.demo.jetweatherapp.model.Weatherimport com.demo.jetweatherapp.network.WeatherApiimport javax.inject.Injectclass WeatherRepository @Inject constructor(    private val api: WeatherApi) {    suspend fun getWeather(cityQuery: String, units: String): DataOrException<Weather, Boolean, Exception> {        val response = try {            api.getWeather(query = cityQuery, units = units)        } catch (e: Exception) {            Log.d("REX", "getWeather - error: ${e.message}")            return DataOrException(                e = e            )        }        Log.d("REX", "getWeather - response: ${response.toString()}")        return DataOrException(data = response)    }}