package com.demo.jetweatherapp.repositoryimport com.demo.jetweatherapp.data.DataOrExceptionimport com.demo.jetweatherapp.model.Weatherimport com.demo.jetweatherapp.network.WeatherApiimport javax.inject.Injectclass WeatherRepository @Inject constructor(    private val api: WeatherApi) {    suspend fun getWeather(cityQuery: String): DataOrException<Weather, Boolean, Exception> {        val response = try {            api.getWeather(query = cityQuery)        } catch (e: Exception) {            return DataOrException(                e = e            )        }        return DataOrException(data = response)    }}