package com.demo.jetweatherapp.screens.aboutScreenimport androidx.compose.foundation.layout.*import androidx.compose.material.icons.Iconsimport androidx.compose.material.icons.filled.ArrowBackimport androidx.compose.material.icons.filled.Infoimport androidx.compose.material3.*import androidx.compose.runtime.Composableimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.res.stringResourceimport androidx.compose.ui.text.font.FontWeightimport androidx.navigation.NavControllerimport com.demo.jetweatherapp.Rimport com.demo.jetweatherapp.widgets.WeatherAppBar@OptIn(ExperimentalMaterial3Api::class)@Composablefun WeatherAboutScreen(navController: NavController) {    Scaffold(topBar = {        WeatherAppBar(            title = "About",            icon = Icons.Default.ArrowBack,            isMainScreen = false,            navController = navController        ) {            navController.popBackStack()        }    }) {        Surface(            modifier = Modifier                .padding(top = it.calculateTopPadding())                .fillMaxSize()        ) {            Column(                horizontalAlignment = Alignment.CenterHorizontally,                verticalArrangement = Arrangement.Center            ) {                Text(                    text = stringResource(id = R.string.abaut_app),                    style = MaterialTheme.typography.titleLarge, fontWeight = FontWeight.Bold                )                Text(                    text = "Api: ${stringResource(id = R.string.api_used)}",                    style = MaterialTheme.typography.titleLarge, fontWeight = FontWeight.Bold                )                Text(                    text = "Contact: ${stringResource(id = R.string.contect_as)}",                    style = MaterialTheme.typography.titleLarge, fontWeight = FontWeight.SemiBold                )            }        }    }}