package com.demo.jetweatherapp.screens.mainScreenimport androidx.compose.material3.CircularProgressIndicatorimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.produceStateimport androidx.hilt.navigation.compose.hiltViewModelimport androidx.navigation.NavControllerimport com.demo.jetweatherapp.data.DataOrExceptionimport com.demo.jetweatherapp.model.Weather@Composablefun WeatherMainScreen(    navController: NavController,    mainViewModel: MainViewModel = hiltViewModel()) {    ShowData(mainViewModel)}@Composablefun ShowData(mainViewModel: MainViewModel) {    val weatherData =        produceState<DataOrException<Weather, Boolean, Exception>>(            initialValue = DataOrException(loading = true)){            value = mainViewModel.getWeatherData(city = "Elche")        }.value    if (weatherData.loading == true) {        CircularProgressIndicator()    } else if (weatherData.data != null) {        Text(text = "Main Screen ${weatherData.data!!.toString()}")    }}