package com.demo.jetweatherapp.widgetsimport androidx.compose.material3.MaterialThemeimport androidx.compose.material3.SmallTopAppBarimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.ui.graphics.vector.ImageVectorimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.text.font.FontWeightimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.Dpimport androidx.compose.ui.unit.dpimport androidx.compose.ui.unit.sp@Preview@Composablefun WeatherAppBar(    title: String = "title",    icon: ImageVector? = null,    isMainScreen: Boolean = false,    elevation: Dp = 2.dp,    //navController: NavController,    onAddActionClicked: () -> Unit = {},    onButtonClicked: () -> Unit = {}) {    SmallTopAppBar(        title = {            Text(                text = title,                color = MaterialTheme.colorScheme.secondary,                style = TextStyle(                    fontWeight = FontWeight.Bold,                    fontSize = 15.sp                )            )        }    )}